import "./style.css";
import { useEffect, useState, Fragment } from "react";
import ListItem from "../../components/ListItem/ListItem";

export default function Home(props) {
const [input, setInput] = useState('');
const [tasksToDo, setToDos] = useState([]);
const [tasksDone, setDone] = useState(["hello"]);

function addTaskToDo() {
  const newToDos = [input, ...tasksToDo];
  setToDos(newToDos);
  setInput("");
}

function doneTask(i) {
  const newToDo = tasksToDo.splice(i, 1);
  setToDos(newToDo);
  const taskDone = tasksToDo[i];
  setDone(taskDone, ...tasksToDo);
}

function undo(i) {
  setDone(tasksDone.filter((task) => task != tasksDone[i]));
  const newToDos = [tasksDone[i], ...tasksToDo];
  setToDos(newToDos);
}


  return (
    <Fragment>
      <h1>What Do I have Planned For Today?</h1>
      <h2 class = "howManyTasks">
        I have {tasksToDo.length} tasks left to do
        </h2>
      <input
          placeholder='Add a to-do'
          value={input}
          name='text'
          className='todo-input'
          onChange={(event) => setInput(event.target.value)}
        ></input>
        <button className='todo-button' onClick={() => addTaskToDo()}>Add task</button>
        <h2>Left To Do:</h2>
        <ul>
          {tasksToDo.map((input, i) => (
          <ListItem
            type = "do"
            index = {i}
            input = {input}
            finish = {doneTask}
            undo = {undo}
          ></ListItem>
          ))}
        </ul>
        <h2>Finished Tasks:</h2>
        <ul>
          {tasksDone.map((input, i) => (
          <ListItem
            type = "done"
            index = {i}
            input = {input}
            finish = {doneTask}
            undo = {undo}
            ></ListItem>
            ))}
        </ul>
    </Fragment>
  );
}
