import "./style.css";
import { useEffect, useState, Fragment } from "react";

export default function Home(props) {
const [input, setInput] = useState('');
const [tasksToDo, setToDos] = useState([]);
const [tasksDone, setDone] = useState([]);

function addTaskToDo() {
  const newToDos = [input, ...tasksToDo];
  setToDos(newToDos);
  setInput("");
}

function doneTask(i) {
  newToDo = tasksToDo.splice(i, 1);
  setToDos(newToDo);
  const taskDone = toDo[i];
  setDone(taskDone, ...tasksToDo);
}

  return (
    <Fragment>
      <h1>What Do I have Planned For Today?</h1>
      <h2 class = "howManyTasks">
        I have {tasksToDo.length} tasks to do
        </h2>
      <input
          placeholder='Add a to-do'
          value={input}
          name='text'
          className='todo-input'
          onChange={(event) => setInput(event.target.value)}
        />
        <button className='todo-button'>Add task</button>


    </Fragment>
  );
}
